$(document).ready(function(){$(".carousel").carousel({interval:!1});var a="#carousel-praxis, #carousel-person, #carousel-therapie, #carousel-kontakt";$(a).hammer().on("swipeleft",function(){$(this).carousel("next")}),$(a).hammer().on("swiperight",function(){$(this).carousel("prev")}),$(".show-more").click(function(){$(this).parent(".carousel").next(".more-block").hasClass("in")?$(this).children(".icon").removeClass("icon-arrow-top").addClass("icon-arrow-down"):$(this).children(".icon").removeClass("icon-arrow-down").addClass("icon-arrow-top")})});